package tanda2;public class Pruebas {public static void main(String[] args) {int numFilas;System.out.println("Introduce el número de filas que desea (Superior a 2)");numFilas = Consola.leeInt();while (numFilas <= 2) {System.out.println("El número "+numFilas+ " no es un número valido, introduce un número superior a 2");numFilas = Consola.leeInt();}int contFilas = numFilas;int columna;while (contFilas >= 0){if (contFilas == numFilas||contFilas == 0){int zDibujadasPrimeraYUltimaFila = numFilas;while (zDibujadasPrimeraYUltimaFila > 0) {System.out.print("Z");zDibujadasPrimeraYUltimaFila--;}contFilas = contFilas - 1;}else {columna = contFilas;while (columna > 0) {System.out.print(" ");columna--;}System.out.print("Z");}contFilas--;System.out.println();}}}